// src/pages/DashboardPage.js

import React from 'react';
import { LayoutDashboard, ListTodo, FileBarChart, LogIn as LogOutIcon, CheckCircle, Send } from 'lucide-react';
import { FOLDER_NAMES } from '../utils/constants'; // Import folder names for clarity

const DashboardPage = ({ userProfile, folders, setCurrentPage, onLogout }) => {
  const FolderCard = ({ title, icon, count, onClick }) => (
    <div
      onClick={onClick}
      className="relative bg-light-bg-card p-6 rounded-2xl shadow-lg hover:shadow-xl flex flex-col items-center justify-center 
                 cursor-pointer transition-transform transform hover:-translate-y-1 hover:scale-105 duration-300 
                 border border-border-subtle-light hover:border-brand-orange-light 
                 dark:bg-dark-bg-card dark:border-border-subtle-dark dark:hover:border-brand-orange-dark 
                 dark:shadow-md dark:hover:shadow-lg" 
      aria-label={`Go to ${title} folder, containing ${count} files`}
      role="button"
    >
      {/* Icon background circle: Reverted to blue-accent, made translucent (glassmorphism) */}
      <div className="mb-4 bg-brand-accent-light bg-opacity-70 backdrop-blur-sm p-4 rounded-full 
                      border border-white/30 shadow-inner 
                      dark:bg-brand-accent-dark dark:bg-opacity-60 dark:border-gray-300/10 
                      transition-colors duration-300">{icon}</div> 
      <h3 className="text-lg font-semibold text-text-primary-light text-center dark:text-text-primary-dark">{title}</h3>
      <p className="text-sm text-text-secondary-light mt-2 dark:text-text-secondary-dark">{count} {count === 1 ? 'file' : 'files'}</p>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-light-bg-canvas to-blue-50 p-8 
                    dark:from-dark-bg-canvas-start dark:via-dark-bg-canvas-via dark:to-dark-bg-canvas-end">
      <div 
        className="max-w-5xl mx-auto bg-light-bg-card rounded-3xl shadow-2xl p-10 
                   border-t-4 border-brand-orange-light 
                   dark:bg-dark-bg-card dark:border-brand-orange-dark 
                   dark:shadow-xl" 
      >
        <div className="flex items-center justify-center mb-6">
          <LayoutDashboard className="w-9 h-9 text-ui-blue-light mr-2 dark:text-ui-blue-dark" />
          <h1 className="text-4xl font-bold text-text-primary-light dark:text-text-primary-dark">Dashboard</h1>
        </div>
        {userProfile && (
          <p className="text-center text-text-secondary-light mb-10 text-lg dark:text-text-secondary-dark">
            Welcome back, <span className="font-bold text-ui-blue-light dark:text-ui-blue-dark">{userProfile.displayName}</span> 
          </p>
        )}

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-8">
          <FolderCard
            title="To be Approved"
            icon={<ListTodo size={36} className="text-status-attention-light dark:text-status-attention-dark" />}
            count={folders[FOLDER_NAMES.INCOMING_INVOICES].length}
            onClick={() => setCurrentPage('incomingInvoices')}
          />
          <FolderCard
            title="Raised in QuickBooks"
            icon={<FileBarChart size={36} className="text-status-raised-light dark:text-status-raised-dark" />}
            count={folders[FOLDER_NAMES.PUBLISHED_DOCUMENTS].length}
            onClick={() => setCurrentPage('publishedDocuments')}
          />
          <FolderCard
            title="Approved Invoices"
            icon={<CheckCircle size={36} className="text-status-approved-light dark:text-status-approved-dark" />}
            count={folders[FOLDER_NAMES.APPROVED_INVOICES].length}
            onClick={() => setCurrentPage('approvedInvoices')}
          />
          <FolderCard
            title="Sent Invoices"
            icon={<Send size={36} className="text-status-pending-light dark:text-status-pending-dark" />}
            count={folders[FOLDER_NAMES.SENT_DOCUMENTS].length}
            onClick={() => setCurrentPage('sentDocuments')}
          />
        </div>

        <div className="mt-12 text-center">
          <button
            onClick={onLogout}
            className="inline-flex items-center text-status-logout-light hover:text-red-700 font-semibold 
                       transition-colors duration-200 dark:text-status-logout-dark dark:hover:text-red-200"
            aria-label="Logout"
          >
            <LogOutIcon className="w-5 h-5 rotate-180 mr-2" />
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
