// src/components/FileCard.js

import React from 'react';
import { Eye, Pencil, CheckCircle, Send, FileText, FileSpreadsheet } from 'lucide-react';

const FileCard = ({ file, onEdit, onApprove, onView, onSent, showApprove = false, showSent = false, isViewAndEdit = false }) => {
  const FileIcon = file.type === 'pdf' ? FileText : FileSpreadsheet;

  return (
    // FIX: The main card wrapper div. Its className was misplaced in previous response.
    // This is the single top-level element returned by the component.
    <div 
      className="bg-light-bg-card bg-opacity-70 backdrop-blur-sm rounded-xl shadow-lg hover:shadow-xl 
                 p-6 flex flex-col items-center text-center transition-transform hover:scale-105 duration-200 
                 h-full justify-between border border-white/30 
                 dark:bg-dark-bg-card dark:bg-opacity-60 dark:border-gray-300/10 dark:text-text-primary-dark"
    >
      {/* This is the first child div within the main card div */}
      <div className="flex flex-col items-center flex-grow w-full">
        <FileIcon className="w-16 h-16 mb-3 text-brand-orange-light dark:text-brand-orange-dark" />
        <h3 className="text-lg font-semibold text-text-primary-light mb-2 break-words w-full h-12 overflow-hidden text-ellipsis line-clamp-2 dark:text-text-primary-dark">
          {file.name}
        </h3>
        <p className="text-sm text-brand-orange-light mt-auto dark:text-brand-orange-dark">{file.type.toUpperCase()} File</p>
      </div>

      {/* This is the second child div within the main card div (adjacent to the first) */}
      <div className="flex flex-wrap justify-center gap-3 w-full min-h-[48px]">
        {isViewAndEdit ? (
          <button
            onClick={() => onView(file)}
            className="flex items-center px-4 py-2 bg-brand-orange-light/10 text-brand-orange-light rounded-lg 
                       hover:bg-brand-orange-light/20 transition-colors text-sm font-medium 
                       dark:bg-brand-orange-dark/10 dark:text-brand-orange-dark dark:hover:bg-brand-orange-dark/20"
            aria-label={`View ${file.name}`}
          >
            <Eye size={16} className="mr-1" /> View {file.type === 'xlsx' && "/Edit"}
          </button>
        ) : file.type === 'xlsx' && onEdit ? (
          <button
            onClick={() => onEdit(file)}
            className="flex items-center px-4 py-2 bg-brand-orange-light/10 text-brand-orange-light rounded-lg 
                       hover:bg-brand-orange-light/20 transition-colors text-sm font-medium 
                       dark:bg-brand-orange-dark/10 dark:text-brand-orange-dark dark:hover:bg-brand-orange-dark/20"
            aria-label={`View or edit ${file.name}`}
          >
            <Pencil size={16} className="mr-1" /> View/Edit
          </button>
        ) : (
          <button
            onClick={() => onView(file)}
            className="flex items-center px-4 py-2 bg-brand-orange-light/10 text-brand-orange-light rounded-lg 
                       hover:bg-brand-orange-light/20 transition-colors text-sm font-medium 
                       dark:bg-brand-orange-dark/10 dark:text-brand-orange-dark dark:hover:bg-brand-orange-dark/20"
            aria-label={`View ${file.name}`}
          >
            <Eye size={16} className="mr-1" /> View
          </button>
        )}

        {showApprove && onApprove && (
          <button
            onClick={() => onApprove(file.id)}
            className="flex items-center px-4 py-2 bg-status-approved-light/10 text-status-approved-light rounded-lg 
                       hover:bg-status-approved-light/20 transition-colors text-sm font-medium 
                       dark:bg-status-approved-dark/10 dark:text-status-approved-dark dark:hover:bg-status-approved-dark/20"
            aria-label={`Approve ${file.name}`}
          >
            <CheckCircle size={16} className="mr-1" /> Approve
          </button>
        )}

        {showSent && onSent && (
          <button
            onClick={() => onSent(file.id)}
            className="flex items-center px-4 py-2 bg-status-pending-light/10 text-status-pending-light rounded-lg 
                       hover:bg-status-pending-light/20 transition-colors text-sm font-medium 
                       dark:bg-status-pending-dark/10 dark:text-status-pending-dark dark:hover:bg-status-pending-dark/20"
            aria-label={`Mark ${file.name} as sent`}
          >
            <Send size={16} className="mr-1" /> Sent?
          </button>
        )}
      </div>
    </div>
  );
};

export default FileCard;
