// src/authConfig.js

// Replace with your actual Azure AD Application (client) ID and Tenant ID
export const msalConfig = {
  auth: {
    clientId: "6b93b79a-c6e2-4bd9-b3fb-dc5c04fcb61c", // REQUIRED: Replace with your Application (client) ID from Azure AD
    authority: "https://login.microsoftonline.com/3bb4d7f8-7011-44c7-bb3b-e869a25ac707", // REQUIRED: Replace with your Tenant ID or "common" for multi-tenant
    redirectUri: "/", // Must be registered as a Redirect URI for your app in Azure AD
  },
  cache: {
    cacheLocation: "sessionStorage", // This stores tokens in browser session storage
    storeAuthStateInCookie: false, // Set to true if you are running on IE11/Legacy Edge.
  },
};

// Scopes for login and API calls
export const loginRequest = {
  scopes: ["User.Read", "Sites.ReadWrite.All"] // Add any other scopes your application needs
};

// You can add more specific API scopes here if needed for different Graph API calls
export const graphConfig = {
  graphMeEndpoint: "https://graph.microsoft.com/v1.0/me"
};
