// src/components/ViewFileModal.js


import React, { useEffect } from 'react';

import { X } from 'lucide-react'; // Import X icon specifically for this component


const ViewFileModal = ({ isOpen, onClose, fileName, fileContent, selectedFileType, selectedFileMimeType }) => {

  useEffect(() => {

    return () => {

      // Revoke the blob URL when the modal closes or fileContent changes

      if (fileContent && typeof fileContent === 'string' && fileContent.startsWith('blob:')) {

        URL.revokeObjectURL(fileContent);

      }

    };

  }, [fileContent]);


  if (!isOpen) return null;


  const renderFileContent = () => {

    if (!fileContent && selectedFileType !== 'xlsx') {

      return <p className="text-gray-600 text-center py-10 dark:text-gray-400">No content to display or file type not supported for in-app viewing.</p>;

    }


    if (selectedFileType === 'pdf') {

      return (

        <iframe

          src={fileContent}

          title={fileName}

          width="100%"

          height="100%"

          style={{ border: 'none' }}

          className="flex-grow"

          aria-label={`PDF viewer for ${fileName}`}

        >

          This browser does not support PDFs. Please download the PDF to view it: <a href={fileContent} download={fileName}>Download PDF</a>

        </iframe>

      );

    } else if (selectedFileType === 'xlsx') {

      return (

        <div className="text-center py-10">

          <p className="text-gray-700 font-semibold mb-4 dark:text-gray-300">Excel files cannot be viewed directly in the app.</p>

          <p className="text-gray-600 dark:text-gray-400">They are opened in Excel Online for the full viewing experience.</p>

          <p className="text-sm text-gray-500 mt-2 dark:text-gray-500">

            (The "View" button opens them in a new tab.)

          </p>

        </div>

      );

    } else if (selectedFileMimeType && selectedFileMimeType.startsWith('image/')) {

      return (

        <img

          src={fileContent}

          alt={fileName}

          className="max-w-full max-h-full object-contain mx-auto"

          aria-label={`Image viewer for ${fileName}`}

        />

      );

    } else {

      return (

        <pre className="bg-gray-100 p-4 rounded-md overflow-auto flex-grow text-gray-800 whitespace-pre-wrap break-words min-h-[200px] dark:bg-gray-700 dark:text-white">

          {fileContent}

        </pre>

      );

    }

  };


  return (

    <div className="fixed inset-0 bg-gray-600 bg-opacity-75 flex items-center justify-center p-4 z-50">

      <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-7xl max-h-[98vh] h-full flex flex-col dark:bg-gray-800 dark:text-white">

        <div className="flex justify-between items-center mb-4">

          <h2 className="text-2xl font-bold text-gray-800 dark:text-white">Viewing: {fileName}</h2>

          <button

            onClick={onClose}

            className="text-gray-500 hover:text-gray-700 rounded-full p-1 transition-colors dark:text-gray-400 dark:hover:text-gray-200"

            aria-label="Close modal"

          >

            <X className="w-6 h-6" />

          </button>

        </div>

        <div className="flex-grow overflow-hidden flex flex-col justify-center items-center h-full">

          {renderFileContent()}

        </div>

        <div className="flex justify-end mt-6">

          <button

            onClick={onClose}

            className="bg-blue-600 text-white font-bold py-2 px-4 rounded-md hover:bg-blue-700 transition-colors shadow-md"

          >

            Close

          </button>

        </div>

      </div>

    </div>

  );

};


export default ViewFileModal;
